using NutaDev.CsLib.Maintenance.Exceptions.Abstract;
using NutaDev.CsLib.Maintenance.Exceptions.Delegates;
using System;
using System.Diagnostics;
using System.IO;
using System.Text;

namespace NutaDev.CsLib.Jokes.Jokes
{
    /// <summary>
    /// Tu~tu~ruuu...mayushii-desu!
    /// </summary>
    public class Tuturu
        : IExceptionHandler
    {
        /// <summary>
        /// Serialized image.
        /// </summary>
        private const string Base64Text = "";

        /// <summary>
        /// Initializes a new instance of the <see cref="Tuturu"/> class.
        /// </summary>
        /// <param name="soundFilePath"></param>
        public Tuturu(string soundFilePath = null)
        {
            SoundFilePath = soundFilePath;
        }

        /// <summary>
        /// Gets or sets the exception handler.
        /// </summary>
        public ExceptionHandlerDelegate ExceptionHandler { get; set; }

        /// <summary>
        /// Gets the absolute path to sound file.
        /// </summary>
        private string SoundFilePath { get; }

        /// <summary>
        /// Decodes the ascii art and returns it.
        /// </summary>
        /// <returns>Decoded ascii art.</returns>
        public static string Get()
        {
            return Encoding.UTF8.GetString(Convert.FromBase64String(Base64Text));
        }

        /// <summary>
        /// Executes the script.
        /// </summary>
        public void Play()
        {
            Console.WriteLine(Get());

            if (File.Exists(SoundFilePath))
            {
                try
                {
                    using (Process process = Process.Start(@"powershell", $@"-c (New-Object Media.SoundPlayer '{SoundFilePath}').PlaySync();"))
                    {
                        process.WaitForExit();
                    }
                }
                catch (Exception ex)
                {
                    ExceptionHandler?.Invoke(ex);
                }
            }
        }
    }
}
